INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('109', '부분집합의 개수,진부분집합의 개수
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('110', '특정한 원소를 갖거나 갖지 않는 부분집합의 개수', '집합 $A=\{a_{1},a_{2},a_{3},\cdot\cdot\cdot,a_n\}$의 원소 중에서 특정한 원소 $k$개를 반드시 원소로 갖는 집합 $A$의 부분집합의 개수는 $2^{n-k}(k<n)$개 이다. 
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('111', '합집합', '합집합이란, 두 집합 $A, B$에 대하여 $A$에 속하거나 $B$에 속하는 모든 원소로 이루어진 집합을 $A$와 $B$의 합집합이라 하며, 이것을 기호로 $A\cup B$와 같이 나타낸다.', '1', '고등-수학-2학-01-01-03');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('112', '합집합과 교집합의 성질', '합집합과 교집합의 성질이란, 두 집합 $A, B$에 대하여 $A\cup\emptyset=A,A\cap\emptyset=\emptyset$이고, $A\cup A=A,A\cap A=A$이며, $A\cup(A\cap B)=A,A\cap(A\cup B)=A$을 만족한다.', '1', '고등-수학-2학-01-01-03');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('113', '전체집합', '전제집합이란, 어떤 집합에 대하여 그 부분집합을 생각할 때, 처음의 집합을 전체집합이라하며 기호로 $U$와 같이 나타낸다.', '1', '고등-수학-2학-01-01-04');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('114', '여집합과 차집합의 성질', '여집합과 차집합의 성질은 전체집합 $U$의 두 부분집합 $A,B$에 대하여 다음을 만족한다.
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('124', '$A\subset B$와 같은 표현', '$A\subset B$와 같은 표현은 $A\cup B=B$,$A\cap B=A$,$A-B=\emptyset$,$B^\complement\subset A^\complement$,$A^\complement\cup B=U$와 같은 표현들이 있다.', '1', '고등-수학-2학-01-01-04');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('129', '합집합의 원소의 개수', '합집합의 원소의 개수는 세 유한집합 $A, B, C$에 대하여 1. $n(A\cup B)=n(A)\cup n(B)-n(A\cap B)$ 
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('135', '조건', '조건이란, 변수를 포함한 문장이나 식이 변수의 값에 따라 참, 거짓이 판별될 때, 그 문장이나 식', '1', '고등-수학-2학-01-02-01');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('137', '진리집합', '전체집합 $U$의 원소 중에서 조건 $p(x)$를 참이 되게 하는 모든 원소의 집합을 조건 $p(x)$의 진리집합이라 하고, 주로 집합 $P$로 나타낸다.', '1', '고등-수학-2학-01-02-01');
