INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('608', '합성수', '(1)$1$보다 큰 자연수 중에서 소수가 아닌 수
