INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('3793', '소인수분해', '소인수분해는 $1$보다 큰 자연수를 그 수의 소인수만의 곱으로 나타내는 것이다.
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('8791', '인수', '자연수 $a$가 자연수 $b$와 $c$의 곱으로 나타내어질 때, 즉 $a=b\times c$일 때, $b,c$를 $a$의 인수라 한다.', '1', '중등-중1-1학-01-01-02');
