INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('3795', '공약수와 최대공약수', '(1)공약수 : 두개 이상의 자연수의 공통인 약수
(2)최대공약수 : 공약수 중에서 가장 큰 수
