INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('5707', '곱셈의 계산법칙(교환법칙,결합법칙)', '세 수 $a,b,c$에 대하여
