INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('996', '$acx^2+(ad+bc)x+bd$의 인수분해방법', '$acx^2+(ad+bc)x+bd=(ax+b)(cx+d)$
