INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('9166', '일차방정식', '방정식의 우변에 있는 모든 항을 좌변으로 이항하여 정리한 식이 (x에 대한 일차식)=0, 즉 ax+b=0 ($ae0$) 꼴로 나타나는 방정식을 x에 대한 일차방정식이라 한다.', '0', '중등-중1-1학-02-02-03');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('6795', '복잡한 일차방정식의 풀이', '(1) 계수가 소수 또는 분수이면 양변에 적당한 수를 곱하여 계수를 정수로 고친다.$\to\begin{cases}{계수 중에 소수가 있을 때\to 방정식의 양변에 10, 100, 1000, \cdot\cdot\cdot과 같은 10의 거듭제곱을 곱한다.} \\ {계수 중에 분수가 있을 때\to 방정식의 양변에 분모의 최소공배수를 곱한다.}\end{cases}$
(2) 괄호가 있으면 괄호를 풀어 정리한다.
(3) x를 포함하는 항은 좌변으로, 상수항은 우변으로 이항한다.
(4) 양변을 정리하여 ax=b ($ae0$) 꼴로 나타낸다.
(5) x의 계수 a로 양변을 나누어 해를 구한다.', '0', '중등-중1-1학-02-02-03');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('6797', '일차방정식 풀이-해가 무수히 많은 경우', '주어진 방정식을 $ax=b$의 꼴로 고쳤을 때 $ax=b$에서 $a=0$이고 $b=0$, 즉 $0\times x=(0)$의 꼴이면 해가 무수히 많다.(해는 모든 수이다.)', '1', '중등-중1-1학-02-02-03');
