INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('4684', '일차부등식의 풀이', '일차부등식은 다음과 같은 순서로 푼다,
