INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('4960', '평행이동', '한 도형을 일정한 방향으로 일정한 거리만큼 이동하는 것을 평행이동이라 한다.', '1', '중등-중2-1학-04-01-01');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('4961', '일차함수 y=ax+b ($ae0$)의 그래프', '일차함수 y=ax+b의 그래프는 일차함수 y=ax의 그래프를 y축의 방향으로 b만큼 평행이동한 직선이다.
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('4962', '일차함수', '함수 y=f(x)에서 y=ax+b (a, b는 상수$ae0$)와 같이 y가 x에 대한 일차식으로 나타내어질 때, 이 함수를 x에 대한 일차함수라 한다.', '0', '중등-중2-1학-04-01-02');
