INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('3905', '부등식', '부등식이란 부등호 $>$, $<$, $\geq$, $\leq$를 사용하여 수나 식의 대소 관계를 나타낸 식 이다.', '1', '고등-수학-2학-01-02-05');
