INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('3907', '조건부등식', '부등식에는 절대부등식과 조건 부등식이 있다. 조건부등식이란 문자를 포함한 부등식에서 그 문자에 어떤 특정한 실수 값을 대입했을 때에만 성립하는 부등식이다.', '1', '고등-수학-2학-01-02-05');
