INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('5241', '치역', '치역이란, 함수 $f\colon X\longrightarrow Y$에서 정의역 $X$의 원소 $x$에 공역 $Y$의 원소 $y$가 대응할 때, 이것을 기호로 $y=f(x)$와 같이 나타내고, $f(x)$를 $x$에서의 함숫값이라 한다. 이때 함수 $f$에서 함숫값 전체의 집합 $\lbrace f(x)\rvert x\in X\rbrace$를 함수 $f$의 치역이라 한다.', '1', '고등-수학-2학-02-01-01');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('5242', '함수의 그래프의 특징', '함수의 그래프의 특징은 함수의 그래프는 정의역의 각 원소 $a$에 대하여 $y$축에 평행한 직선 $x=a$와 오직 한 점에서 만난다.', '1', '고등-수학-2학-02-01-01');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('5243', '일대일함수의 그래프의 특징', '일대일함수의 그래프의 특징은 일대일함수는 정의역의 서로 다른 두 원소에 대응하는 공역의 원소가 항상 서로 달라야 한다. 따라서 일대일함수의 그래프는 치역의 각 원소 $k$에 대하여 $x$축에 평행한 직선 $y=k$와 오직 한 점에서 만난다.', '1', '고등-수학-2학-02-01-02');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('5245', '네 개 이상의 유리식의 계산', '네 개 이상의 유리식의 계산은 게산 과정이 간단해지도록 적당히 두 개씩 묶어서 계산한다.', '1', '고등-수학-2학-02-02-01');
