INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('5248', '유리함수의 정의역', '유리함수에서 정의역이 주어지지 않은 경우에는 분모가 $0$이 되지 않도록 하는 실수 전체의 집합을 정의역으로 한다.', '1', '고등-수학-2학-02-02-01');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('5249', '무리식의 값이 실수가 되기 위한 조건', '무리식의 값이 실수가 되려면 근호 안의 식의 값이 양수 또는 $0$이어야 하고, 분모는 $0$이 아니어야 한다.', '1', '고등-수학-2학-02-02-03');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('5250', '분모의 유리화', '분모의 유리화는 $a>0,b>0$일 때, $\frac{a}{\sqrt[]{b}}=\frac{a\sqrt[]{b}}{\sqrt[]{b}\sqrt[]{b}}=\frac{a\sqrt[]{b}}{b}$이고, $\frac{c}{\sqrt[]{a}+\sqrt[]{b}}=\frac{c(\sqrt[]{a}-\sqrt[]{b})}{(\sqrt[]{a}+\sqrt[]{b})(\sqrt[]{a}-\sqrt[]{b})}=\frac{c(\sqrt[]{a}-\sqrt[]{b})}{a-b}(ae b)$,  $\frac{c}{\sqrt[]{a}-\sqrt[]{b}}=\frac{c(\sqrt[]{a}+\sqrt[]{b})}{(\sqrt[]{a}-\sqrt[]{b})(\sqrt[]{a}+\sqrt[]{b})}=\frac{c(\sqrt[]{a}+\sqrt[]{b})}{a-b}(ae b)$이다.', '1', '고등-수학-2학-02-02-03');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('5251', '무리함수의 정의역', '무리함수의 정의역이 주어지지 않은 경우에는 함숫값이 실수가 되도록 하는 실수 전체의 집합을 정의역으로 생각한다.', '1', '고등-수학-2학-02-02-03');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('5254', '순열의 수', '서로 다른 $n$개에서 $r$개를 택하는 순열의 수는 $_nP_r=n(n-1)(n-2)\times\cdots\times(n-r+1)(0<r\leq n)$이다.', '1', '고등-수학-2학-03-02-01');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('5255', '$n!$을 이용한 순열의 수', '$n!$을 이용한 순열의 수는 $_nP_n=n!$,$_nP_0=1$,$0!=1$이 성립하며, $_nP_r=\frac{n!}{(n-r)!}(0\leq r\leq n)$을 만족한다.', '1', '고등-수학-2학-03-02-01');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('5256', '조합의 수', '서로 다른 $n$개에서 $r$개를 택하는 조합의 수는 $_nC_r=\frac{_nP_r}{r!}=\frac{n!}{r!(n-r)!}$(단, $0\leq r\leq n$)', '1', '고등-수학-2학-03-02-02');
