INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('9660', '삼각형의 합동조건(SAS합동)', '대응하는 두 변의 길이가 각각 같고, 그 끼인각의 크기가 같을 때(SAS 합동)
