INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('7123', '마름모의 성질', '마름모의 두 대각선은 서로 다른 것을 수직이등분한다.', '1', '중등-중2-2학-01-02-02');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('7125', '정사각형', '네 변의 길이가 모두 같고, 네 내각의 크기가 모두 같은 사각형', '0', '중등-중2-2학-01-02-02');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('1120', '평행사변형이 마름모가 되는 조건', '평행사변형이 다음 조건 중 어느 하나를 만족시키면 마름모가 된다.
