INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('7128', '마름모가 정사각형이 되는 조건', '마름모가 다음 중 어느 한 조건을 만족시키면 정사각형이 된다.
