INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('7167', '사다리꼴', '한 쌍의 대변이 평행한 사각형', '0', '중등-중2-2학-01-02-02');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('7168', '등변사다리꼴', '아랫변의 양 끝 각의 크기가 같은 사다리꼴', '1', '중등-중2-2학-01-02-02');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('7169', '등변사다리꼴의 성질', '(1) 평행하지 않은 한 쌍의 대변의 길이가 같다. 
