INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('1182', '입체도형에서 닮음의 성질', '닮은 두 입체도형에서
