INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('1183', '닮음비', '닮은 두 도형에서 대응변 또는 대응하는 모서리의 길이의 비', '0', '중등-중2-2학-02-01-01');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('2714', '삼각형의 중선', '중선 : 삼각형에서 한 꼭짓점과 그 대변의 중점을 이은 선분', '0', '중등-중2-2학-02-02-02');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('2715', '삼각형의 무게중심', '삼각형의 세 중선의 교점', '0', '중등-중2-2학-02-02-02');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('2716', '삼각형의 중선의 성질', '삼각형의 중선은 그 삼각형의 넒이를 이등분한다.', '1', '중등-중2-2학-02-02-02');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('2717', '삼각형의 무게중심의 성질', '(1) 삼각형의 세 중선은 한 점(무게중심)에서 만난다.
