INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('2718', '삼각형의 무게중심과 넓이의 관계', '$\triangle ABC$에서 점 G가 무게중심일 때
