INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('2602', '평행사변형의 넓이', '평행사변형 ABCD의 이웃하는 두 변의 길이가 a, b이고 그 끼인각 x가 예각일 때, 평행사변형 ABCD의 넓이 S는 $S=ab\sin x$', '0', '중등-중3-2학-01-01-04');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('9923', '사각형의 넓이', '사각형 ABCD의 두 대각선의 길이가 a, b이고 두 대각선이 이루는 각 x가 예각일 때, 사각형 ABCD의 넓이 S는 $S=\frac{1}{2}ab\sin x$', '0', '중등-중3-2학-01-01-04');
