INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('2607', '삼각형의 내접원', '반지름의 길이가 r인 원 O가 $\triangle ABC$의 내접원이고 세 점 D, E, F가 접점일 때
