INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('3175', '두 직선의 위치 관계', '공간에서 서로 다른 두 직선의 위치 관계는 다음 세 가지 경우가 있다.
