INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('7960', '분수식', '다항식이 아닌 유리식을 분수식이라고 한다.', '1', '고등-수학-2학-02-02-01');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('7962', '통분', '유리식의 성질 (1)을 이용하여  두 개 이상의 유리식을 분모가 같은 유리식으로 고치는 것을 통분이라고 한다.
