INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('7963', '약분', '유리식의 분자와 분모에 공약수가 있을 때, 유리식의 성질(2)를 이용하여 분자와 분모를 공약수로 나누어 식을 간단히 하는 것을 약분이라고 한다.
