INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('7971', '분수함수', '함수$y=f(x)$에서 $f(x)$가 $x$에 대한 분수식으로 나타내어진 유리함수를 분수함수라 한다.', '1', '고등-수학-2학-02-02-01');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('7977', '제곱근', '$a\geq0$일때, 제곱하여 $a$가 되는 수를 $a$의 제곱근이라 한다. 이 중에서 음수가 아닌 것을 $\sqrt[]{a}$, 음수인 것을 $-\sqrt[]{a}$로 나타내고,  이를 한꺼번에 $\pm\sqrt[]{a}$라고 나타낸다.', '1', '고등-수학-2학-02-02-02');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('7978', '제곱근의 성질 (1)', '$a\geq0$일때, 다음이 성립한다.
