INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('7979', '제곱근의 성질 (2)', '$a^2$의 양의 제곱근, 즉 $\sqrt{a^2}$에 대하여 다음과 같이 성립한다.
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('7983', '무리수가 서로 같은 조건', '$a$, $b$, $c$, $d$가 유리수 이고 $\sqrt{m}$,$\sqrt{n}$이 무리수 일때, 다음이 성립한다.
(1)$a+b\sqrt{m}=0$ $ \Longleftrightarrow $ $a=0$, $b=0$
(2)$a+b\sqrt{m}=c+d\sqrt{m}$ $ \Longleftrightarrow $  $a=c$, $b=d$
(3)$a+\sqrt{m}=b+\sqrt{n}$ $ \Longleftrightarrow $ $a=b$, $m=n$
(4) $a\sqrt{2}+b\sqrt{3}=c\sqrt{2}+d\sqrt{3}$ $ \Longleftrightarrow $ $a=c$, $b=d$', '1', '고등-수학-2학-02-02-03');
