INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('8424', '대표를 뽑는 경우의 수를 구하는 방법', '(1) 자격이 다른 대표를 뽑는 경우
