INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('8425', '확률', '각 경우가 일어날 가능성이 모두 같은 어떤 실험이나 관찰을 여러 번 반복할 때, 어떤 사건이 일어나는 상대도수가 일정한 값에 가까워지면 이 일정한 값은 일어나는 모든 경우의 수에 대한 어떤 사건이 일어나는 경우의 수의 비율과 같고, 이를 그 사건이 일어날 확률이라 한다.', '0', '중등-중2-2학-04-02-01');
