INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('3277', '거듭제곱근의 성질', '$a>o,b>0$이고 $m,n$이 $2$이상의 정수일 때, 다음이 성립한다.
