INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('3333', '$a^0$과 $a^{-n}$의 정의', '$ae0$이고 $n$이 양의 정수일 때, $a^0$과 $a^{-n}$을 다음과 같이 정의한다.
