INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('3334', '지수법칙-지수가 정수일때', '$ae0,be0$이고 $m,n$이 정수일 때, 다음이 성립한다.
