INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('3358', '지수법칙-지수가 유리수일 때', '$a>0,b>0$이고 $p,q$가 유리수일 때, 다음이 성립한다.
