INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('9541', '$S_n=Ar^n+B$ ($A$, $r$, $B$는 상수)를 합으로 갖는 수열 $\lbrace a_n\rbrace$', '$S_n=Ar^n+B$ ($A$, $r$, $B$는 상수)를 합으로 갖는 수열 $\lbrace a_n\rbrace$은 
(1) $A+B=0$이면 첫째항부터 등비수열
(2) $A+Be0$이면 제2항부터 등비수열', '1', '고등-수1-전체-03-01-03');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('6877', '기호 $\sum $', '수열 ${a_n}$
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('6878', '$\sum $의 성질', '기호 $\sum $에 대하여 다음 성질이 성립한다.
(1) $\sum^n_{k\mathop=1}(a_k+b_k)=\sum^n_{k\mathop=1}{a_k}+\sum^n_{k\mathop=1}{b_k} $
(2) $\sum^n_{k\mathop=1}(a_k-b_k)=\sum^n_{k\mathop=1}{a_k}-\sum^n_{k\mathop=1}{b_k}$
(3) $\sum^n_{k\mathop=1}{ca_k}=c\sum^n_{k\mathop=1}{a_k}$ (단, $c$는 상수)
(4) $\sum^n_{k\mathop=1}c=cn$ (단, $c$는 상수)', '1', '고등-수1-전체-03-02-01');
INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('1199', '$\sum $를 이용하여 수열의 합을 구하는 방법', '(1) 일반항$a_n$을 구한다.
(2) 일반항$a_n$에서 $n$대신 $k$를 대입하여 $a_k$를 구한다.
(3) $S_n=\sum ^n_{k\mathop{=}1}a_k$를 이용한다.', '1', '고등-수1-전체-03-02-01');
