INSERT INTO  UK_MASTER(UK_ID, UK_NAME, UK_DESCRIPTION, TRAIN_UNSEEN, CURRICULUM_ID) VALUES('4669', '전개', '단항식과 다항식의 곱셈에서 괄호를 풀어 하나의 다항식으로 나타내는 것을 전개한다고 하며, 전개하여 얻은 다항식을 전개식이라 한다.', '0', '중등-중2-1학-02-02-02');
